// src/modules/Job/ListAllJobs.js

import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom'; // For navigation
import JobService from '../..../services/JobService'; // Adjust path based on your structure

const ListAllJobs = () => {
  const [jobsList, setJobsList] = useState([]); // State to store list of jobs
  const [recruiterName, setRecruiterName] = useState(''); // State to store recruiter name
  const navigate = useNavigate(); // Hook for navigation

  useEffect(() => {
    // Fetch all jobs when the component mounts
    const fetchJobs = async () => {
      try {
        const response = await JobService.getAllJobs(); // Fetch jobs
        setJobsList(response.data); // Update state with fetched data
        setRecruiterName(localStorage.getItem('recruiterUName') || ''); // Set recruiter name from localStorage
      } catch (err) {
        alert(err.message); // Handle errors
      }
    };

    fetchJobs();
  }, []);

  const closeJob = async (jobId) => {
    try {
      await JobService.closeJob(jobId); // Close job
      alert('Job closed successfully');
      setJobsList((prevJobs) => prevJobs.filter((job) => job.jobId !== jobId)); // Remove job from list
    } catch (err) {
      alert(err.message); // Handle errors
    }
  };

  const receivedAppls = (jobId) => {
    navigate(`/viewApps/${jobId}`); // Navigate to view applications
  };

  return (
    <div className="container-fluid overflow-hidden">
      <div className="text-center">
        <h4>List of Jobs posted by {recruiterName}</h4>
        <div className="row">
          <div className="col-3"></div>
          <div className="col-6 card">
            <table className="table table-striped table-primary table-hover">
              <thead>
                <tr>
                  <th>Job Code</th>
                  <th>Job Title</th>
                  <th>Job Description</th>
                  <th>Skill</th>
                  <th colspan="2">Actions</th>
                </tr>
              </thead>
              <tbody>
                {jobsList.map((job) => (
                  <tr key={job.jobId}>
                    <td>{job.jobId}</td>
                    <td>{job.jobTitle}</td>
                    <td>{job.jobDescription}</td>
                    <td>{job.skillName}</td>
                    <td>
                      <button
                        className="btn btn-warning"
                        onClick={() => closeJob(job.jobId)}
                        disabled={!job.jobStatus}
                      >
                        Close Job
                      </button>
                    </td>
                    <td>
                      <button
                        className="btn btn-info"
                        onClick={() => receivedAppls(job.jobId)}
                        disabled={!job.jobStatus}
                      >
                        Received Applications
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
          <div className="col-3"></div>
        </div>
      </div>
    </div>
  );
};

export default ListAllJobs;
