import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import * as BookmarkedFreelancerService from '../../../../services/BookmarkedFreelancerService';
import * as FreelancerService from '../../../../services/FreelancerService';

const AddBookmarkFreelancer = () => {
  const navigate = useNavigate();
  const [freelancers, setFreelancers] = useState([]);
  const [selectedFreelancer, setSelectedFreelancer] = useState('');

  useEffect(() => {
    // Fetch freelancers when component mounts
    FreelancerService.listAll()
      .then(response => setFreelancers(response))
      .catch(error => console.error('Error fetching freelancers:', error));
  }, []);

  const handleAddBookmark = () => {
    if (selectedFreelancer) {
      // Add bookmark logic
      BookmarkedFreelancerService.addBookmarkFreelancer({ freelancerId: selectedFreelancer })
        .then(() => {
          // Handle success
          navigate('/listBookmarks');
        })
        .catch(error => console.error('Error adding bookmark:', error));
    } else {
      alert('Please select a freelancer to add as a bookmark.');
    }
  };

  return (
    <div className="container">
      <h1>Add Bookmark Freelancer</h1>
      <div className="form-group">
        <label htmlFor="freelancerSelect">Select Freelancer</label>
        <select
          id="freelancerSelect"
          className="form-control"
          value={selectedFreelancer}
          onChange={(e) => setSelectedFreelancer(e.target.value)}
        >
          <option value="">Select Freelancer</option>
          {freelancers.map(freelancer => (
            <option key={freelancer.id} value={freelancer.id}>
              {freelancer.firstName} {freelancer.lastName}
            </option>
          ))}
        </select>
      </div>
      <button className="btn btn-primary" onClick={handleAddBookmark}>Add Bookmark</button>
    </div>
  );
};

export default AddBookmarkFreelancer;
