import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './ListJobs.css';

const ListJobs = () => {
  const [activeJobsList, setActiveJobsList] = useState([]);
  const freelancerId = Number(localStorage.getItem('freelancerId'));
  const navigate = useNavigate();

  useEffect(() => {
    fetch('/api/jobs/active') // Replace with your actual API endpoint
      .then(response => response.json())
      .then(data => {
        setActiveJobsList(data);
      })
      .catch(err => {
        alert('Error: ' + err.message);
      });
  }, []);

  const addBookmark = (jobId, skillId) => {
    const bookmarkedJob = {
      jobId,
      skillId,
      freelancerId
    };

    fetch('/api/bookmarkedjobs', { // Replace with your actual API endpoint
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(bookmarkedJob)
    })
      .then(response => response.json())
      .then(data => {
        alert('Bookmarked Successfully');
      })
      .catch(err => {
        alert('Error: ' + err.message);
      });
  };

  return (
    <div className="container-fluid">
      <h3 className="text-center">Active Jobs list</h3>
      <div className="row">
        <div className="col-3"></div>
        <div className="col-6 card">
          <table className="table table-striped table-hover table-warning text-center">
            <thead>
              <tr>
                <th>ID</th>
                <th>Job Title</th>
                <th>Job Description</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {activeJobsList.map(job => (
                <tr key={job.jobId}>
                  <td>{job.jobId}</td>
                  <td>{job.jobTitle}</td>
                  <td>{job.jobDescription}</td>
                  <td>
                    <button className="btn btn-success" onClick={() => addBookmark(job.jobId, job.skillId)}>
                      Bookmark
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
        <div className="col-3"></div>
      </div>
    </div>
  );
};

export default ListJobs;
