import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Updated import
import './AddSkill.css';

const AddSkill = () => {
  const [skillName, setSkillName] = useState('');
  const [skillDescription, setSkillDescription] = useState('');
  const navigate = useNavigate(); // Updated hook

  const handleSubmit = (e) => {
    e.preventDefault();

    const newSkill = {
      name: skillName,
      description: skillDescription
    };

    fetch('skills', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(newSkill)
    })
      .then(response => response.json())
      .then(data => {
        console.log(data);
        alert('Skill created successfully');
        // Redirect or reset form
        navigate('/admin'); // Optional redirection after successful submission
      })
      .catch(err => {
        alert('Error creating skill: ' + err.message);
      });
  };

  const handleGoBack = () => {
    navigate('/admin'); // Updated navigation
  };

  return (
    <div className="container-fluid">
      <div className="row mt-3 text-center">
        <div className="col-3"></div>
        <div className="col-6">
          <div className="card border-5 shadow bg-gradient bg-light">
            <h5 className="card-title mt-3">Add a new Skill</h5>
            <div className="card-body">
              <form className="form" onSubmit={handleSubmit}>
                <div className="form-group">
                  <div className="input-group">
                    <span className="input-group-text">Name</span>
                    <input
                      className="form-control"
                      type="text"
                      placeholder="Skill Name"
                      value={skillName}
                      onChange={(e) => setSkillName(e.target.value)}
                      required
                    />
                  </div>
                  <div className="input-group mt-1">
                    <span className="input-group-text">Description</span>
                    <input
                      className="form-control"
                      type="text"
                      placeholder="Skill Description"
                      value={skillDescription}
                      onChange={(e) => setSkillDescription(e.target.value)}
                      required
                    />
                  </div>
                  <div className="row mt-3 text-center">
                    <div className="col-5"></div>
                    <div className="col-2">
                      <div className="input-group">
                        <button className="btn btn-outline-success" type="submit">
                          Create Skill
                        </button>
                      </div>
                    </div>
                    <div className="col-5"></div>
                  </div>
                </div>
              </form>
            </div>
          </div>
        </div>
        <div className="col-3"></div>
      </div>
      <div className="row text-center mt-5">
        <div className="col-4"></div>
        <div className="col-4">
          <button className="btn btn-outline-danger" onClick={handleGoBack}>
            Go Back
          </button>
        </div>
        <div className="col-4"></div>
      </div>
    </div>
  );
};

export default AddSkill;
