import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './ListBookmarkedJobs.css';

const ListBookmarkedJobs = () => {
  const [bookmarksList, setBookmarksList] = useState([]);
  const [freelancerName, setFreelancerName] = useState('');
  const freelancerId = Number(localStorage.getItem('freelancerId'));
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch freelancer name
    fetch(`/api/freelancers/${freelancerId}`) // Replace with your actual API endpoint
      .then(response => response.json())
      .then(data => {
        setFreelancerName(data.userName);
        localStorage.setItem('freelancerUName', data.userName);
      })
      .catch(err => {
        alert('Error: ' + err.message);
      });

    // Fetch bookmarked jobs
    fetch(`/api/bookmarkedjobs/${freelancerId}`) // Replace with your actual API endpoint
      .then(response => response.json())
      .then(data => {
        setBookmarksList(data);
      })
      .catch(err => {
        alert('Error: ' + err.message);
      });
  }, [freelancerId]);

  const applyToJob = (jobId) => {
    navigate(`apply/${jobId}`); // Navigate to the apply route
  };

  return (
    <div className="container-fluid">
      <h5 className="text-center">List of Bookmarks by {freelancerName}</h5>
      <table className="table table-striped table-hover table-primary text-center table-responsive">
        <thead>
          <tr>
            <th>Bookmark Id</th>
            <th>Job Title</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {bookmarksList.map(job => (
            <tr key={job.id}>
              <td>{job.id}</td>
              <td>{job.jobName}</td>
              <td>
                <button className="btn btn-warning" onClick={() => applyToJob(job.jobId)}>
                  Apply to Job
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ListBookmarkedJobs;
