import React, { useEffect, useState } from 'react';
import * as FreelancerService from '../../../../services/FreelancerService';
import './ListFreelancers.css'; // Ensure to create this CSS file if needed

const ListFreelancers = () => {
  const [freelancersList, setFreelancersList] = useState([]);
  const recruiterId = localStorage.getItem('recruiterId');
  console.log("Inside list of freelancers");

  useEffect(() => {
    // Fetch all freelancers
    const fetchFreelancers = async () => {
      try {
        const response = await FreelancerService.listAll();
        setFreelancersList(response);
      } catch (error) {
        alert(error.response?.data || 'An error occurred');
      }
    };

    fetchFreelancers();
  }, []);

  const addBookmark = (freelancerId) => {
    console.log("Add Bookmark Method Invoked");
    // Assuming you have a separate service for bookmarks if not handled in FreelancerService
    const bookmarkedFreelancer = {
      freelancerId: freelancerId,
      recruiterId: Number(recruiterId)
    };

    // Update to use the correct service or endpoint for bookmarking
    // Replace `BookmarkedFreelancerService` with the appropriate service if necessary
    // Example:
    // BookmarkService.addBookmarkFreelancer(bookmarkedFreelancer)
    //   .then(response => {
    //     console.log(response);
    //     alert('Bookmark added successfully');
    //   })
    //   .catch(error => {
    //     console.log(error);
    //     alert('Failed to add bookmark');
    //   });
  };

  return (
    <div className="container-fluid">
      <h3 className="text-center">Freelancers List</h3>
      <div className="row">
        <div className="col-3"></div>
        <div className="col-6 card">
          <table className="table table-striped table-hover table-warning text-center">
            <thead>
              <tr>
                <th>ID</th>
                <th>First Name</th>
                <th>Last Name</th>
                <th>User Name</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {freelancersList.map(freelancer => (
                <tr key={freelancer.id}>
                  <td>{freelancer.id}</td>
                  <td>{freelancer.firstName}</td>
                  <td>{freelancer.lastName}</td>
                  <td>{freelancer.userName}</td>
                  <td>
                    <button className="btn btn-success" onClick={() => addBookmark(freelancer.id)}>
                      Bookmark
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
        <div className="col-3"></div>
      </div>
    </div>
  );
};

export default ListFreelancers;
