import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom'; // Updated import
import './Admin.css';

const Admin = () => {
  const navigate = useNavigate(); // Updated hook

  useEffect(() => {
    // Check if the page has been reloaded before
    if (!sessionStorage.getItem('reloaded')) {
      // Set a flag in sessionStorage to indicate that the page has been reloaded
      sessionStorage.setItem('reloaded', 'true');
      // Refresh the page
      window.location.reload();
    }
  }, []); // Empty dependency array ensures this runs only once when the component mounts

  return (
    <div className="container-fluid">
      <div className="row g-3 mt-3 text-center">
        <div className="col-lg-3 col-md-4 col-sm-6">
          <div className="card border-5 shadow bg-gradient bg-light card-large">
            <h5 className="card-title">Manage Freelancers</h5>
            <div className="card-body">
              <button className="btn btn-outline-success" onClick={() => navigate('/admin/freelancers')}>
                List all Freelancers
              </button>
            </div>
          </div>
        </div>
        <div className="col-lg-6 col-md-4 col-sm-6">
          <div className="card border-5 shadow bg-gradient bg-light card-large">
            <h5 className="card-title">Manage Skills</h5>
            <div className="card-body">
              <div className="mb-1">
                <button className="btn btn-outline-success" onClick={() => navigate('/admin/skills/add')}>
                  Add new Skill
                </button>
              </div>
              <div className="mb-1">
                <button className="btn btn-outline-dark" onClick={() => navigate('/admin/skills')}>
                  List all Skills
                </button>
              </div>
            </div>
          </div>
        </div>
        <div className="col-lg-3 col-md-4 col-sm-6">
          <div className="card border-5 shadow bg-gradient bg-light card-large">
            <h5 className="card-title">Manage Recruiters</h5>
            <div className="card-body">
              <button className="btn btn-outline-warning" onClick={() => navigate('/admin/recruiters')}>
                List all Recruiters
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Admin;
