// src/modules/Job/ListApplicants.js

import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import JobService from '../../../../servicesJobService'; // Adjust path based on your structure
import JobApplicationService from '../../services/JobApplicationService'; // Adjust path based on your structure

const ListApplicants = () => {
  const { jobId } = useParams(); // Get jobId from URL parameters
  const [applicantsList, setApplicantsList] = useState([]); // State to store list of applicants
  const navigate = useNavigate(); // Hook for navigation

  useEffect(() => {
    // Fetch applicants for the given job ID
    const fetchApplicants = async () => {
      try {
        const response = await JobApplicationService.getAllApplicants(jobId); // Fetch applicants
        setApplicantsList(response.data); // Update state with fetched data
      } catch (err) {
        alert(err.message); // Handle errors
      }
    };

    fetchApplicants();
  }, [jobId]);

  // Function to award a job to a freelancer
  const awardJob = async (jobId, freelancerId) => {
    try {
      await JobService.awardJob(jobId, freelancerId); // Call service to award job
      alert('Job Awarded successfully');
      navigate('../list'); // Navigate to list view
    } catch (err) {
      console.log(err.message); // Handle errors
    }
  };

  return (
    <div className="container-fluid">
      <div className="row text-center">
        <div className="col-3"></div>
        <div className="col-6">
          <div className="card">
            <h4 className="card-title">List of Job Applicants</h4>
            <div className="card-body">
              <table className="table table-hover table-striped table-success">
                <thead>
                  <tr>
                    <th>Id</th>
                    <th>Full Name</th>
                    <th>UserId</th>
                    <th colspan="2">Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {applicantsList.map((applicant, index) => (
                    <tr key={applicant.id}>
                      <td>{index + 1}</td>
                      <td>{applicant.freelancerName}</td>
                      <td>{applicant.freelancerUName}</td>
                      <td>
                        <button
                          className="btn btn-outline-success"
                          onClick={() => awardJob(applicant.jobId, applicant.freelancerId)}
                        >
                          Award Job
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
        <div className="col-3"></div>
      </div>
    </div>
  );
};

export default ListApplicants;
