// src/modules/Recruiter/FindBookmarkFreelancerById.js

import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios'; // Make sure you have axios installed or use fetch API
import './FindBookmarkFreelancerById.css'; // Ensure to create this CSS file if needed

const FindBookmarkFreelancerById = () => {
  const { id } = useParams();
  const [bookmarkedFreelancer, setBookmarkedFreelancer] = useState({
    id: 0,
    userName: '',
    name: '',
    skillName: [],
    skillExp: [],
  });
  const [frExistsById, setFrExistsById] = useState(false);

  useEffect(() => {
    axios.get(`your-api-url/freelancer/${id}`)
      .then(response => {
        const data = response.data;
        setFrExistsById(true);
        setBookmarkedFreelancer({
          id: data.freelancer.id,
          name: `${data.freelancer.firstName} ${data.freelancer.lastName}`,
          userName: data.freelancer.userName,
          skillName: data.freelancer.skills.map(skill => skill.skill.name),
          skillExp: data.freelancer.skills.map(skill => skill.years),
        });
      })
      .catch(error => {
        setFrExistsById(false);
        alert(error.response?.data || 'An error occurred');
      });
  }, [id]);

  return (
    <div className="text-center">
      <div className="row">
        <div className="col-3"></div>
        <div className="col-6">
          <div className="card">
            <h5 className="card-title">Bookmarked Freelancer Details</h5>
            <div className="card-body">
              <table className="table table-hover table-primary table-responsive">
                <thead>
                  <tr>
                    <th>Freelancer Name</th>
                    <th>Freelancer UserId</th>
                  </tr>
                </thead>
                <tbody>
                  <tr>
                    <td>{bookmarkedFreelancer.name}</td>
                    <td>{bookmarkedFreelancer.userName}</td>
                  </tr>
                </tbody>
              </table>
              <h5>Skills</h5>
              <table className="table table-warning table-bordered border-danger table-hover table-sm table-responsive">
                <thead>
                  <tr>
                    <th>Skill</th>
                    <th>Experience (in Years)</th>
                  </tr>
                </thead>
                <tbody>
                  {bookmarkedFreelancer.skillName.map((name, index) => (
                    <tr key={index}>
                      <td>{name}</td>
                      <td>{bookmarkedFreelancer.skillExp[index]}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
        <div className="col-3"></div>
      </div>
    </div>
  );
};

export default FindBookmarkFreelancerById;
