// src/Freelancer/FindJobById/FindJobById.js
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import './FindJobById.css';

const FindJobById = () => {
  const { id } = useParams(); // Use useParams to get the route parameter
  const [bookmarkedJob, setBookmarkedJob] = useState(null);
  const [jobExistsById, setJobExistsById] = useState(true);

  useEffect(() => {
    fetch(`/api/bookmarkedjobs/${id}`) // Replace with your actual API endpoint
      .then(response => response.json())
      .then(data => {
        setBookmarkedJob(data);
      })
      .catch(err => {
        alert('Error: ' + err.message);
        setJobExistsById(false);
      });
  }, [id]);

  if (!jobExistsById) {
    return <div className="text-center">Job not found.</div>;
  }

  return (
    <div className="text-center">
      <div className="row">
        <div className="col-3"></div>
        <div className="col-6">
          <div className="card">
            <h5 className="card-title">Bookmarked Job Details</h5>
            <div className="card-body">
              <table className="table table-hover table-primary table-responsive">
                <thead>
                  <tr>
                    <th>Bookmark Id</th>
                    <th>Job Title</th>
                  </tr>
                </thead>
                <tbody>
                  {bookmarkedJob && (
                    <tr>
                      <td>{bookmarkedJob.id}</td>
                      <td>{bookmarkedJob.job.jobTitle}</td>
                    </tr>
                  )}
                </tbody>
              </table>
              <h5>Skills Required</h5>
              <table className="table table-warning table-bordered border-danger table-hover table-sm table-responsive">
                <thead>
                  <tr>
                    <th>Skill</th>
                    <th>Description</th>
                  </tr>
                </thead>
                <tbody>
                  {bookmarkedJob && bookmarkedJob.skill && (
                    <tr>
                      <td>{bookmarkedJob.skill.name}</td>
                      <td>{bookmarkedJob.skill.description}</td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
          </div>
        </div>
        <div className="col-3"></div>
      </div>
    </div>
  );
};

export default FindJobById;
