// src/modules/Job/AddJob.js

import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import * as JobService from '../../../../services/JobService'; // Adjust path as necessary
import './AddJob.css'; // Ensure this CSS file exists if needed

const AddJob = () => {
  const [jobTitle, setJobTitle] = useState('');
  const [jobDescription, setJobDescription] = useState('');
  const [skillId, setSkillId] = useState('');
  const [skills, setSkills] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch all skills when the component mounts
    const fetchSkills = async () => {
      try {
        // Replace this with actual service method if available
        const response = await JobService.getAllSkills(); 
        setSkills(response);
      } catch (error) {
        alert('Error fetching skills: ' + error.message);
      }
    };

    fetchSkills();
  }, []);

  const handleSubmit = async (event) => {
    event.preventDefault();
    const postJob = {
      jobTitle,
      jobDescription,
      skillId,
      recruiterId: Number(localStorage.getItem('recruiterId')),
      freelancerId: Number('2') // Assuming this is hardcoded for now
    };

    try {
      const response = await JobService.addJob(postJob);
      alert(response);
      navigate('/jobs'); // Adjust the path as needed
    } catch (error) {
      console.warn('Error posting job: ' + error.message);
    }
  };

  return (
    <div className="container-fluid overflow-hidden text-center">
      <h4>Create A New Job Opening</h4>
      <div className="row">
        <div className="col-3"></div>
        <div className="col-6 card bg-light bg-gradient border border-5 shadow">
          <div className="card-body">
            <form className="form" onSubmit={handleSubmit}>
              <div className="input-group mb-3">
                <span className="input-group-text">Job Title</span>
                <input
                  className="form-control"
                  type="text"
                  name="jobTitle"
                  id="jobTitle"
                  value={jobTitle}
                  onChange={(e) => setJobTitle(e.target.value)}
                />
              </div>
              <div className="input-group mb-3">
                <span className="input-group-text">Job Description</span>
                <textarea
                  className="form-control"
                  name="jobDescription"
                  id="jobDescription"
                  value={jobDescription}
                  onChange={(e) => setJobDescription(e.target.value)}
                />
              </div>
              <div className="input-group mb-3">
                <span className="input-group-text">Skill</span>
                <select
                  className="form-select"
                  name="skillList"
                  id="skillList"
                  value={skillId}
                  onChange={(e) => setSkillId(e.target.value)}
                >
                  <option value="">Select a skill</option>
                  {skills.map(skill => (
                    <option key={skill.id} value={skill.id}>{skill.name}</option>
                  ))}
                </select>
              </div>
              <div className="input-group d-grid gap-2 col-6 mx-auto">
                <button className="btn btn-outline-primary" type="submit">Post Job</button>
              </div>
            </form>
          </div>
        </div>
        <div className="col-3"></div>
      </div>
    </div>
  );
};

export default AddJob;
