import React, { useState } from 'react';
import { Route, Routes, Link, useNavigate } from 'react-router-dom';
import './App.css';
import Home from './components/Home/Home';
import AboutUs from './components/AboutUs/AboutUs';
import Login from './components/Login/Login';
import Footer from './components/Footer/Footer';
import AdminRouting from './modules/Admin/AdminRouting';
import RecruiterRouting from './modules/Recruiter/RecruiterRouting';
import FreelancerRouting from './modules/Freelancer/FreelancerRouting';
import ResponsiveAppBar from './components/ResponsiveAppBar'; // Import ResponsiveAppBar

function App() {
  const [loggedIn, setLoggedIn] = useState(Boolean(localStorage.getItem('loggedIn')));
  const navigate = useNavigate();

  const logOut = () => {
    localStorage.clear();
    setLoggedIn(false);
    navigate('/');
  };

  return (
    <div>
      <ResponsiveAppBar logOut={logOut} loggedIn={loggedIn} /> {/* Use ResponsiveAppBar */}
      <Routes>
        <Route path="/"  />
        <Route path="/about" element={<AboutUs />} />
        <Route path="/login" element={<Login />} />
        <Route path="/admin/*" element={<AdminRouting />} />
        <Route path="/recruiter/*" element={<RecruiterRouting />} />
        <Route path="/freelancer/*" element={<FreelancerRouting />} />
      </Routes>
      <Footer />
    </div>
  );
}

export default App;
