import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Updated import
import { getAllSkills } from './SkillService'; // Import the SkillService
import './Skills.css';

const Skills = () => {
  const [skillsList, setSkillsList] = useState([]);
  const navigate = useNavigate(); // Updated hook

  useEffect(() => {
    // Fetch skills using SkillService
    getAllSkills()
      .then(data => setSkillsList(data))
      .catch(err => alert('Error fetching data: ' + err.message));
  }, []);

  const goBack = () => {
    navigate('/admin'); // Updated navigation
  };

  return (
    <div className="container-fluid">
      <div className="row g-3 text-center mt-3">
        <div className="col-3"></div>
        <div className="col-6">
          <div className="card border-5 shadow bg-light bg-gradient">
            <h5 className="card-title mt-3"><b>Skills List</b></h5>
            <div className="card-body">
              <table className="table table-hover table-striped table-success table-bordered">
                <thead>
                  <tr>
                    <th>Unique Id</th>
                    <th>Skill Name</th>
                    <th>Description</th>
                  </tr>
                </thead>
                <tbody>
                  {skillsList.map(skill => (
                    <tr key={skill.id}>
                      <td>{skill.id}</td>
                      <td>{skill.name}</td>
                      <td>{skill.description}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
        <div className="col-3"></div>
      </div>
      <div className="row text-center mt-3">
        <div className="col-4"></div>
        <div className="col-4">
          <button className="btn btn-outline-danger shadow" onClick={goBack}>
            Go Back
          </button>
        </div>
        <div className="col-4"></div>
      </div>
    </div>
  );
};

export default Skills;
