// src/modules/Recruiter/BookmarkedFreelancer/AddBookmarkFreelancer/AddBookmarkFreelancer.js

import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import BookmarkedFreelancerService from '../../../services/BookmarkedFreelancerService';
import FreelancerService from '../../../../services/FreelancerService';

const AddBookmarkFreelancer = () => {
  const navigate = useNavigate();
  const [freelancers, setFreelancers] = useState([]);
  const [selectedFreelancer, setSelectedFreelancer] = useState('');

  useEffect(() => {
    // Fetch freelancers when component mounts
    FreelancerService.getFreelancers()
      .then(response => setFreelancers(response.data))
      .catch(error => console.error('Error fetching freelancers:', error));
  }, []);

  const handleAddBookmark = () => {
    // Add bookmark logic
    BookmarkedFreelancerService.addBookmark(selectedFreelancer)
      .then(response => {
        // Handle success (e.g., navigate to another page)
        navigate('/recruiter/bmark/freelancer/list');
      })
      .catch(error => console.error('Error adding bookmark:', error));
  };

  return (
    <div className="add-bookmark-freelancer">
      <h1>Add Bookmark Freelancer</h1>
      <select
        value={selectedFreelancer}
        onChange={(e) => setSelectedFreelancer(e.target.value)}
      >
        <option value="">Select Freelancer</option>
        {freelancers.map(freelancer => (
          <option key={freelancer.id} value={freelancer.id}>
            {freelancer.name}
          </option>
        ))}
      </select>
      <button onClick={handleAddBookmark}>Add Bookmark</button>
    </div>
  );
};

export default AddBookmarkFreelancer;
