import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Freelancer.css';

const Freelancer = () => {
  const navigate = useNavigate();

  useEffect(() => {
    const hasReloaded = localStorage.getItem('hasReloaded');
    if (!hasReloaded) {
      localStorage.setItem('hasReloaded', 'true');
      window.location.reload();
    }
  }, []);

  return (
    <div className="freelancer-container">
      <div className="freelancer-row">
        <div className="freelancer-card">
          <h5 className="freelancer-card-title">Bookmark Jobs</h5>
          <div className="freelancer-card-body">
            <button className="btn btn-outline-success" onClick={() => navigate('/bmark/job/listJobs')}>
              List Active Jobs
            </button>
            <button className="btn btn-outline-success" onClick={() => navigate('/bmark/job/list')}>
              List Bookmarked Jobs
            </button>
            <button className="btn btn-outline-success" onClick={() => navigate('/bmark/job/add')}>
              Add Bookmark
            </button>
          </div>
        </div>
        <div className="freelancer-card">
          <h5 className="freelancer-card-title">Skills</h5>
          <div className="freelancer-card-body">
            <button className="btn btn-outline-primary" onClick={() => navigate('/skills')}>
              Explore Skills
            </button>
            <button className="btn btn-outline-primary" onClick={() => navigate('/skill')}>
              Add Skill
            </button>
          </div>
        </div>
        <div className="freelancer-card">
          <h5 className="freelancer-card-title">Job Applications</h5>
          <div className="freelancer-card-body">
            <button className="btn btn-outline-warning" onClick={() => navigate('/bmark/job/apply/:id')}>
              Apply for Job
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Freelancer;
