import React from 'react';
import { useForm } from 'react-hook-form';
import { useNavigate } from 'react-router-dom';
import { addAdmin } from '../../services/AdminService';
import { addRecruiter } from './RecruiterService'; // Import recruiter service
import { addFreelancer } from './FreelancerService'; // Import freelancer service
import './Login.css';

const Login = () => {
  const { register, handleSubmit, formState: { errors }, setError } = useForm();
  const navigate = useNavigate();

  const onSubmit = async (data) => {
    try {
      const { role, loginId, password } = data;
      let response;

      if (role === '1') {
        // Add Admin
        response = await addAdmin({
          userName: loginId,
          firstName: '', // Add a default or user-provided firstName
          lastName: '',  // Add a default or user-provided lastName
          password: password
        });

        if (response.password === password) {
          localStorage.setItem('loggedIn', "true");
          localStorage.setItem('adminUName', loginId);
          navigate('/admin-dashboard');
        } else {
          setError('password', { type: 'manual', message: 'Invalid Login Details!' });
        }
      } else if (role === '2') {
        // Add Recruiter
        response = await addRecruiter({
          userName: loginId,
          firstName: '', // Add a default or user-provided firstName
          lastName: '',  // Add a default or user-provided lastName
          password: password
        });

        if (response.password === password) {
          localStorage.setItem('loggedIn', "true");
          localStorage.setItem('recruiterUName', loginId);
          localStorage.setItem('recruiterId', response.id); // Assuming response contains the ID
          navigate('/recruiter-dashboard');
        } else {
          setError('password', { type: 'manual', message: 'Invalid Login Details!' });
        }
      } else if (role === '3') {
        // Add Freelancer
        response = await addFreelancer({
          userName: loginId,
          firstName: '', // Add a default or user-provided firstName
          lastName: '',  // Add a default or user-provided lastName
          password: password
        });

        if (response.password === password) {
          localStorage.setItem('loggedIn', "true");
          localStorage.setItem('freelancerUName', loginId);
          localStorage.setItem('freelancerId', response.id); // Assuming response contains the ID
          navigate('/freelancer-dashboard');
        } else {
          setError('password', { type: 'manual', message: 'Invalid Login Details!' });
        }
      }
    } catch (err) {
      console.error(err);
      if (err.response && err.response.status === 400) {
        setError('loginId', { type: 'manual', message: err.response.data });
      }
    }
  };

  return (
    <div className="login-container">
      <div className="login-form">
        <form onSubmit={handleSubmit(onSubmit)}>
          <div className="form-group">
            <label htmlFor="loginId">UserName</label>
            <input
              type="text"
              id="loginId"
              className={`form-control ${errors.loginId ? 'is-invalid' : ''}`}
              placeholder="userName"
              {...register('loginId', { required: 'Please enter a userName.' })}
            />
            <div className="invalid-feedback">{errors.loginId?.message}</div>
          </div>
          <div className="form-group">
            <label htmlFor="passwordField">Password</label>
            <input
              type="password"
              id="passwordField"
              className={`form-control ${errors.password ? 'is-invalid' : ''}`}
              placeholder="password"
              {...register('password', { required: 'Please enter a password.' })}
            />
            <div className="invalid-feedback">{errors.password?.message}</div>
          </div>
          <div className="form-group">
            <label htmlFor="userRole">Select Role</label>
            <select
              id="userRole"
              className={`form-select ${errors.role ? 'is-invalid' : ''}`}
              {...register('role', { required: 'Please choose a valid role.' })}
            >
              <option value="1">Admin</option>
              <option value="2">Recruiter</option>
              <option value="3">Freelancer</option>
            </select>
            <div className="invalid-feedback">{errors.role?.message}</div>
          </div>
          <div className="form-group">
            <button type="submit" className="btn btn-success">Login</button>
            <button type="reset" className="btn btn-dark">Reset</button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Login;
