// src/Freelancer/CreateBookmark/CreateBookmark.js
import React, { useState } from 'react';
import { addJob } from '../../../../services/JobService'; // Adjust the import path if necessary
import './CreateBookmark.css';

const CreateBookmark = () => {
  const [bookmarkName, setBookmarkName] = useState('');
  const [bookmarkDescription, setBookmarkDescription] = useState('');

  const handleNameChange = (e) => setBookmarkName(e.target.value);
  const handleDescriptionChange = (e) => setBookmarkDescription(e.target.value);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const newJob = {
      jobTitle: bookmarkName,
      jobDescription: bookmarkDescription,
      // Add other required fields as necessary
    };

    try {
      await addJob(newJob);
      alert('Bookmark created!');
      setBookmarkName('');
      setBookmarkDescription('');
    } catch (error) {
      alert('Error creating bookmark: ' + error.message);
    }
  };

  return (
    <div className="container-fluid">
      <div className="row mt-3 text-center">
        <div className="col-3"></div>
        <div className="col-6">
          <div className="card border-5 shadow bg-gradient bg-light">
            <h5 className="card-title mt-3">Create a New Bookmark</h5>
            <div className="card-body">
              <form onSubmit={handleSubmit}>
                <div className="form-group">
                  <div className="input-group mb-3">
                    <span className="input-group-text">Name</span>
                    <input
                      className="form-control"
                      type="text"
                      placeholder="Bookmark Name"
                      value={bookmarkName}
                      onChange={handleNameChange}
                      required
                    />
                  </div>
                  <div className="input-group mb-3">
                    <span className="input-group-text">Description</span>
                    <input
                      className="form-control"
                      type="text"
                      placeholder="Bookmark Description"
                      value={bookmarkDescription}
                      onChange={handleDescriptionChange}
                      required
                    />
                  </div>
                  <div className="row mt-3 text-center">
                    <div className="col-5"></div>
                    <div className="col-2">
                      <button className="btn btn-outline-success" type="submit">
                        Create Bookmark
                      </button>
                    </div>
                    <div className="col-5"></div>
                  </div>
                </div>
              </form>
            </div>
          </div>
        </div>
        <div className="col-3"></div>
      </div>
    </div>
  );
};

export default CreateBookmark;
