// src/modules/Job/FindJobById.js

import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom'; // To handle route parameters
import JobService from '../../../../services/JobService'; // Adjust path based on your structure

const FindJobById = () => {
  const { jobId } = useParams(); // Get jobId from route params
  const [job, setJob] = useState(null); // State to store job details
  const [loading, setLoading] = useState(true); // State to handle loading
  const [error, setError] = useState(null); // State to handle errors

  useEffect(() => {
    // Fetch job details when component mounts
    const fetchJob = async () => {
      try {
        const response = await JobService.getJobById(jobId); // Fetch job details
        setJob(response.data); // Update job state with fetched data
      } catch (err) {
        setError(err.message); // Handle errors
      } finally {
        setLoading(false); // Set loading to false once data is fetched
      }
    };

    fetchJob();
  }, [jobId]); // Dependency array to re-run effect if jobId changes

  if (loading) {
    return <p>Loading...</p>; // Loading state
  }

  if (error) {
    return <p>Error: {error}</p>; // Error state
  }

  return (
    <div>
      <h1>Job Details</h1>
      {job ? (
        <div>
          <h2>{job.jobTitle}</h2>
          <p><strong>Description:</strong> {job.jobDescription}</p>
          <p><strong>Skill:</strong> {job.skillName}</p>
          {/* Add more job details as needed */}
        </div>
      ) : (
        <p>No job found with ID: {jobId}</p>
      )}
    </div>
  );
};

export default FindJobById;
