// src/modules/Recruiter/ListBookmarks.js

import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios'; // Make sure you have axios installed or use fetch API
import './ListBookmarks.css'; // Ensure to create this CSS file if needed

const ListBookmarks = () => {
  const [bookmarksList, setBookmarksList] = useState([]);
  const [recruiterName, setRecruiterName] = useState('');
  const recruiterId = localStorage.getItem('recruiterId');
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch recruiter details
    axios.get(`your-api-url/recruiter/${recruiterId}`)
      .then(response => {
        setRecruiterName(response.data.userName);
        localStorage.setItem('recruiterUName', response.data.userName);
      })
      .catch(error => {
        alert(error.response?.data || 'An error occurred');
      });

    // Fetch all bookmarks
    axios.get('your-api-url/bookmarks')
      .then(response => {
        setBookmarksList(response.data);
      })
      .catch(error => {
        alert(error.response?.data || 'An error occurred');
      });
  }, [recruiterId]);

  const manageFeedbacks = (freelancerUName) => {
    console.log(freelancerUName);
    navigate(`/feedbacks/${freelancerUName}`);
  };

  return (
    <div className="container-fluid">
      <h5 className="text-center">List of Bookmarks by {recruiterName}</h5>
      <table className="table table-striped table-primary text-center table-responsive">
        <thead>
          <tr>
            <th>Bookmark Id</th>
            <th>Freelancer Name</th>
            <th>Freelancer UserName</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {bookmarksList.map((fr) => (
            <tr key={fr.id}>
              <td>{fr.id}</td>
              <td>{fr.freelancerName}</td>
              <td>{fr.freelancerUName}</td>
              <td>
                <button className="btn btn-warning" onClick={() => manageFeedbacks(fr.freelancerUName)}>
                  Manage Feedbacks
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ListBookmarks;
