// src/Freelancer/BookmarkJob/BookmarkJob.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { findJobById } from '../../services/JobService'; // Adjust the import path if necessary
import './BookmarkJob.css';

const BookmarkJob = () => {
  const [bkdJobId, setBkdJobId] = useState('');
  const navigate = useNavigate();

  const handleIdChange = (e) => setBkdJobId(e.target.value);

  const listJobs = () => {
    navigate('/bmark/job/listJobs');
  };

  const findById = async () => {
    localStorage.setItem('bkdJobId', bkdJobId);
    console.log('Fetch Details of Bookmark Number:', Number(localStorage.getItem('bkdJobId')));

    try {
      const data = await findJobById(bkdJobId);
      alert('Bookmark Present');
      navigate(`/bmark/job/find/${bkdJobId}`);
    } catch (err) {
      alert('Error: ' + err.message);
      navigate('/freelancer/bmark/job');
    }
  };

  const goBack = () => {
    navigate('/freelancer');
  };

  return (
    <div className="container-fluid overflow-hidden">
      <div className="row mt-4">
        <div className="col p-3">
          <div className="card shadow border-5 rounded-3 text-center">
            <div className="card-body">
              <h5 className="card-title">Bookmark a Job</h5>
              <button className="btn btn-success" onClick={listJobs}>List Active Jobs</button>
            </div>
          </div>
        </div>
        <div className="col p-3">
          <div className="card shadow border-5 rounded-3 text-center">
            <div className="card-body">
              <h5 className="card-title">Find a Bookmark By Id</h5>
              <input
                className="form-control"
                type="number"
                id="bkdJobId"
                name="bkdJobId"
                placeholder="Bookmarked Job Id"
                value={bkdJobId}
                onChange={handleIdChange}
              />
              <button className="btn btn-primary mt-2" onClick={findById}>Find By Id</button>
            </div>
          </div>
        </div>
        <div className="col p-3">
          <div className="card shadow border-5 rounded-3 text-center">
            <div className="card-body">
              <h5 className="card-title">List Bookmarks</h5>
              <button className="btn btn-warning" onClick={() => navigate('/freelancer/bmark/job/list')}>View List</button>
            </div>
          </div>
        </div>
      </div>
      <div className="row text-center mt-5">
        <div className="col-5"></div>
        <div className="col-2">
          <button className="btn btn-danger" onClick={goBack}>Go Back</button>
        </div>
        <div className="col-5"></div>
      </div>
      <hr />
    </div>
  );
};

export default BookmarkJob;
