import React, { useEffect, useState } from 'react';
import { useHistory } from 'react-router-dom';
import './Recruiters.css';

const Recruiters = () => {
  const [recruitersList, setRecruitersList] = useState([]);
  const history = useHistory();

  useEffect(() => {
    // Replace this URL with your actual API endpoint
    fetch('/api/recruiters')
      .then(response => response.json())
      .then(data => setRecruitersList(data))
      .catch(err => alert('Error fetching data: ' + err.message));
  }, []);

  const goBack = () => {
    history.push('/admin');
  };

  return (
    <div className="container-fluid">
      <div className="row g-3 text-center mt-3">
        <div className="col-3"></div>
        <div className="col-6">
          <div className="card border-5 shadow bg-light bg-gradient">
            <h5 className="card-title mt-3"><b>Recruiters List</b></h5>
            <div className="card-body">
              <table className="table table-hover table-striped table-success table-bordered">
                <thead>
                  <tr>
                    <th>Unique Id</th>
                    <th>First Name</th>
                    <th>Last Name</th>
                    <th>User Name</th>
                  </tr>
                </thead>
                <tbody>
                  {recruitersList.map(recruiter => (
                    <tr key={recruiter.id}>
                      <td>{recruiter.id}</td>
                      <td>{recruiter.firstName}</td>
                      <td>{recruiter.lastName}</td>
                      <td>{recruiter.userName}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
        <div className="col-3"></div>
      </div>
      <div className="row text-center mt-3">
        <div className="col-4"></div>
        <div className="col-4">
          <button className="btn btn-outline-danger shadow" onClick={goBack}>
            Go Back
          </button>
        </div>
        <div className="col-4"></div>
      </div>
    </div>
  );
};

export default Recruiters;
