import React from 'react';
import { useForm } from 'react-hook-form';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Login.css'; // Ensure you have appropriate styles

const Login = ({ setLoggedIn }) => {
  const { register, handleSubmit, formState: { errors }, setError } = useForm();
  const navigate = useNavigate();

  const handleLogin = async (data) => {
    try {
      const { role, username, password } = data;
      let endpoint = '';

      switch (role) {
        case '1':
          endpoint = 'http://localhost:8080/admin/login';
          break;
        case '2':
          endpoint = 'http://localhost:8080/recruiter/login';
          break;
        case '3':
          endpoint = 'http://localhost:8080/freelancer/login';
          break;
        default:
          throw new Error('Invalid role selected');
      }

      const response = await axios.post(endpoint, { username, password });

      if (response.data.valid) {
        localStorage.setItem('loggedIn', 'true');
        localStorage.setItem(`${role}UName`, username);
        localStorage.setItem(`${role}Id`, response.data.id);
        setLoggedIn(true); // Trigger state update in App
        navigate(`/${role === '1' ? 'admin' : role === '2' ? 'recruiter' : 'freelancer'}`);
      } else {
        setError('password', { type: 'manual', message: 'Invalid Login Details!' });
      }
    } catch (err) {
      console.error(err);
      if (err.response) {
        if (err.response.status === 400) {
          setError('username', { type: 'manual', message: err.response.data.message });
        } else if (err.response.status === 401) {
          setError('password', { type: 'manual', message: 'Invalid credentials!' });
        }
      }
    }
  };

  return (
    <div className="login-container">
      <h2>Login</h2>
      <form onSubmit={handleSubmit(handleLogin)} className="login-form">
        <div className="form-group">
          <label htmlFor="username">Username:</label>
          <input
            id="username"
            type="text"
            {...register('username', { required: 'Username is required' })}
          />
          {errors.username && <p className="invalid-feedback">{errors.username.message}</p>}
        </div>
        <div className="form-group">
          <label htmlFor="password">Password:</label>
          <input
            id="password"
            type="password"
            {...register('password', { required: 'Password is required' })}
          />
          {errors.password && <p className="invalid-feedback">{errors.password.message}</p>}
        </div>
        <div className="form-group">
          <label htmlFor="role">Role:</label>
          <select id="role" {...register('role', { required: 'Role is required' })}>
            <option value="1">Admin</option>
            <option value="2">Recruiter</option>
            <option value="3">Freelancer</option>
          </select>
          {errors.role && <p className="invalid-feedback">{errors.role.message}</p>}
        </div>
        <button type="submit" className="btn btn-success">Login</button>
      </form>
    </div>
  );
};

export default Login;
