import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Recruiter.css'; // Ensure to create this CSS file if needed

const Recruiter = () => {
  const navigate = useNavigate();

  useEffect(() => {
    console.log('Inside recruiter');
  }, []);

  const handleLogOut = () => {
    localStorage.clear();
    navigate('/').then(() => window.location.reload());
  };

  return (
    <div>
      <div className="container-fluid row">
        <div className="card shadow border-5 rounded-3 col text-center">
          <div className="card-body">
            <h5 className="card-title">Bookmark Freelancers</h5>
            <p className="card-text">Manage your Bookmarked Freelancers</p>
            <button className="btn btn-success" onClick={() => navigate('/recruiter/bmark/freelancer')}>
              Manage Bookmarks
            </button>
          </div>
        </div>
        <div className="card shadow border-5 rounded-3 col text-center">
          <div className="card-body">
            <h5 className="card-title">Jobs</h5>
            <p className="card-text">Manage your Posted Jobs</p>
            <button className="btn btn-warning" onClick={() => navigate('/recruiter/jobs')}>
              Manage Jobs
            </button>
          </div>
        </div>
      </div>
      <div className="row mt-3 text-center">
        <div className="col-4"></div>
        <div className="col-4">
          <button className="btn btn-danger" onClick={handleLogOut}>
            Log Out
          </button>
        </div>
        <div className="col-4"></div>
      </div>
    </div>
  );
};

export default Recruiter;
