import React, { useState } from 'react';
import { Route, Routes, Link, useNavigate } from 'react-router-dom';
import './App.css';
import logo from './logo.svg';
import Home from './components/Home/Home';
import AboutUs from './components/AboutUs/AboutUs';
import Login from './components/Login/Login';
import Footer from './components/Footer/Footer';

function App() {
  const [loggedIn, setLoggedIn] = useState(Boolean(localStorage.getItem('loggedIn')));
  const navigate = useNavigate();

  const logOut = () => {
    localStorage.clear();
    setLoggedIn(false);
    navigate('/');
  };

  return (
    <div>
      <nav className="navbar navbar-expand-md navbar-dark bg-dark">
        <div className="container-fluid">
          <Link className="navbar-brand" to="/">Online Job Portal</Link>
          <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent"
            aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
            <span className="navbar-toggler-icon"></span>
          </button>

          <div className="collapse navbar-collapse" id="navbarSupportedContent">
            <ul className="navbar-nav me-auto mb-2 mb-lg-0">
              <li className="nav-item">
                <Link className="nav-link active" to="/">Home</Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link" to="/about">About Us</Link>
              </li>
              {!loggedIn ? (
                <li className="nav-item">
                  <Link className="nav-link" to="/login">Login</Link>
                </li>
              ) : (
                <li className="nav-item">
                  <a className="nav-link" onClick={logOut}>Log Out</a>
                </li>
              )}
            </ul>
          </div>
        </div>
      </nav>
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/about" element={<AboutUs />} />
        <Route path="/login" element={<Login />} />
        <Route path="/admin/*" element={<AdminRouting />} />
          <Route path="/recruiter/*" element={<RecruiterRouting />} />
          <Route path="/freelancer/*" element={<FreelancerRouting />} />
      </Routes>
      <Footer />
    </div>
  );
}

export default App;
